import config from "@config";
import Axios from "axios";
import { parseCookies } from "nookies";
export const axios = Axios.create({
  baseURL: config.baseURL
});
export const serverAxios = ({
  req,
  res
}) => {
  const axios = Axios.create({
    baseURL: config.baseURL,
    headers: {
      authorization: `Bearer ${parseCookies({
        req
      }).nsid}`
    }
  });
  axios.interceptors.response.use(res => res, err => {
    if ((err === null || err === void 0 ? void 0 : err.response.status) === 401) {
      res.writeHead(302, {
        Location: "/signIn"
      });
      res.end();
    }

    return Promise.reject(err);
  });
  return axios;
};
export const axiosFetcher = url => axios.get(url).then(res => res.data);
export const cookieOption = {
  maxAge: 3 * 24 * 3600,
  // 쿠키 3일
  path: "/",
  httpOnly: true,
  secure: false
};
export const getThumbnail = fileId => fileId ? `${config.baseURL}/file/thumbnail/${fileId}` : undefined;
